pragma solidity ^0.5.0;
contract Election {
    // Read/writecandidate
    string public candidate;
    //string public admin1 = "771c75b2306389e84e9f027129b5773511ac03dd14975d929d7d66e04586e8f9";
    //string public admin2 = "f86b7c1f83769b3df674dbd858ba2786a6a95d008e55db8a55bdc51c88479b1f";
    // Model a Candidate
    struct Candidate {
        uint id;
        string name;
        uint voteCount;
    }

   

    // Read/write Candidates
    mapping(uint => Candidate) public candidates;

    // Store Candidates Count
    uint public candidatesCount;

    // Store accounts that have voted
    mapping(address => bool) public voters;

    // Constructor
    constructor() public {
        //candidate = "Candidate 1";

        addCandidate("Achref");
        addCandidate("Achref1");
        addCandidate("Achref2");
    }
    
    function addCandidate (string memory _name) public {
        candidatesCount ++;
        candidates[candidatesCount] = Candidate(candidatesCount, _name, 0);
    }


    function vote (uint _candidateId) public {
        // require that they haven't voted before
        require(!voters[msg.sender]);

        // require a valid candidate
        require(_candidateId > 0 && _candidateId <= candidatesCount);

        // record that voter has voted
        voters[msg.sender] = true;

        // update candidate vote Count
        candidates[_candidateId].voteCount ++;

        // trigger voted event
        emit votedEvent(_candidateId);

    }

    event votedEvent (
        uint indexed _candidateId
    );
}
